import React, {useState} from 'react';


const App = () => {
  const [count, changeCount] = useState(0)//状態の定義
  return (
    <>
      <p>カウント:{count}</p>
      <input 
        type="button"
        value="10"
        onClick={() => changeCount(10)}       
      />
    </>
  )
}
