  return (
    <>
      <p>カウント:{count}</p>
      <p>カウント:{name}</p>
      <input 
        type="button"
        value="increment"
        onClick={() => changeCount(prevCount => prevCount+1)}  //無名関数の引数には直前の状態が引き渡される     
      />
    </>
  )
}
