import React, {useState} from 'react';

const Counter = () => {
  const [count, setCount] = useState(0);
  const onCountUp = () => {
    setCount(count + 1);
  };
  const onCountDown = () => {
    setCount(count - 1);
  };
  return (
    <React.Fragment>
      <p>Countï¼š {count} </p>
      <button className onClick={onCountUp}>Increment</button>
      <button className onClick={onCountDown}>Decrement</button>
    </React.Fragment>

  );
};

export default Counter;
