import React, { useState } from 'react';
import "./styles.css";
import { InputTodo } from './components/InputTodo';
import { IncompleteTodos } from './components/IncompleteTodos';

//Reactで状態が変化するものはStateで定義

export const App = () => {
  const [todoText, setTodoText] = useState('');
  const [deadline, setDedline] = useState('')
  const [incompleteTodos, setIncompleteTodos] = useState([]);
  const [doingTodos, setDoingTodos] = useState([]);
  const [completeTodos, setCompleteTodos] = useState([]);
  const [deadlines, setDeadlines] = useState([]);
  const onChangeTodoText = (e) => setTodoText(e.target.value);
  const onChangeDeadline = (e) => setDedline(e.target.value);
  //現在日時stateで管理する？
  const year = new Date().getFullYear();
  const month = new Date().getMonth()+1;
  const date = new Date().getDate();

  const onClickAdd = () => {
    if (todoText === '' || deadline === '') return;
    const newTodos = [...incompleteTodos, todoText];
    const newDeadline = [...deadlines, deadline];
    setIncompleteTodos(newTodos);
    setTodoText('');
    setDeadlines(newDeadline);
    setDedline('');
  }

  const onClickStart = (index) => {
    const newIncompleteTodos = [...incompleteTodos];
    const newDeadline = [...deadlines];
    newIncompleteTodos.splice(index, 1);
    newDeadline.splice(index, 1);

    const newDoingTodos = [...doingTodos, incompleteTodos[index]];
    setIncompleteTodos(newIncompleteTodos);
    setDoingTodos(newDoingTodos);
  }
  const onClickComplete = (index) => {
    const newDoingTodos = [...doingTodos];
    const newDeadline = [...deadlines];
    newDoingTodos.splice(index, 1);
    newDeadline.splice(index, 1);

    const newCompleteTodos = [...completeTodos, doingTodos[index]];
    setDoingTodos(newDoingTodos);
    setCompleteTodos(newCompleteTodos);
  }

  const onClickDelete = (index) => {
    const newTodos = [...incompleteTodos];
    const newDeadline = [...deadlines];
    newTodos.splice(index, 1);
    newDeadline.splice(index, 1);
    setIncompleteTodos(newTodos);
    setDeadlines(newDeadline);
  }






  return (
    <>
      <InputTodo todoText={todoText} deadline={deadline} onChangeTodoText={onChangeTodoText} onChangeDeadline={onChangeDeadline} onClick={onClickAdd}/>
      <IncompleteTodos todos={incompleteTodos} onClickStart={onClickStart} />
      <div className="ing-area">
        <p>進行中Todoリスト</p>
        <ol>
        {doingTodos.map((todo, index) => {
            return(
              <li>
                <div key={todo} className="list-row">
                  <h4>タイトル</h4>
                  <p>{todo}</p>
                  <h4 className='deadline'>期限</h4>
                  <p>{deadlines[index]}</p>
                  <button onClick={() => onClickComplete(index)}>完了</button>
                  <button>変更</button>
                  <button onClick={() => onClickDelete(index)}>削除</button>
                </div>
              </li>
            );
          })}
        </ol>
      </div>
      <div className="complete-area">
        <p>完了したTodoリスト</p>
        <ol>
          {completeTodos.map((todo, index) => {
            return(
              <li>
              <div key={todo} className="list-row">
                <p>{todo}</p>
                <p>{year + '/' + month + '/' + date}</p>
              </div>
            </li>
            );
          })}
        </ol>
      </div>
    </>
  );
};
