import React, { useState } from 'react';
import "./styles.css";

//Reactで状態が変化するものはStateで定義

export const App = () => {
  const [todoText, setTodoText] = useState('');
  const [deadline, setDedline] = useState('')
  const [incompleteTodos, setIncompleteTodos] = useState([]);
  const [completeTodos, setCompleteTodos] = useState([]);
  const [deadlines, setDeadlines] = useState([]);
  const onChangeTodoText = (e) => setTodoText(e.target.value);
  const onChangeDeadline = (e) => setDedline(e.target.value);
  const onClickAdd = () => {
    if (todoText === '' || deadline === '') return;
    const newTodos = [...incompleteTodos, todoText];
    const newDeadline = [...deadlines, deadline];
    setIncompleteTodos(newTodos);
    setTodoText('');
    setDeadlines(newDeadline);
    setDedline('');
  }

  const onClickComplete = (index) => {
    const newIncompleteTodos = [...incompleteTodos];
    const newDeadline = [...deadlines];
    newIncompleteTodos.splice(index, 1);
    newDeadline.splice(index, 1);

    const newCompleteTodos = [...completeTodos, incompleteTodos[index]];
    setIncompleteTodos(newIncompleteTodos);
    setCompleteTodos(newCompleteTodos);
  }

  const onClickDelete = (index) => {
    const newTodos = [...incompleteTodos];
    const newDeadline = [...deadlines];
    newTodos.splice(index, 1);
    newDeadline.splice(index, 1);
    setIncompleteTodos(newTodos);
    setDeadlines(newDeadline);
  }






  return (
    <>
      <div className="input-area">
        <input placeholder="タイトル"  value={todoText} onChange={onChangeTodoText} />
        <input placeholder="期限"  value={deadline} onChange={onChangeDeadline} />
        <button onClick={onClickAdd}>追加</button>
      </div>
      <div className="incomplete-area">
        <p>未着手Todoリスト</p>
        <ol>
          {incompleteTodos.map((todo, index) => {
            return(
              <li>
                <div key={todo} className="list-row">
                  <h4>タイトル</h4>
                  <p>{todo}</p>
                  <h4 className='deadline'>期限</h4>
                  <p>{deadlines[index]}</p>
                  <button onClick={() => onClickComplete(index)}>開始</button>
                  <button>変更</button>
                  <button onClick={() => onClickDelete(index)}>削除</button>
                </div>
              </li>
            );
          })}

        </ol>
      </div>
      <div className="ing-area">
        <p>進行中Todoリスト</p>
        <ol>
          <li>
            <div>
              <p>TODO</p>
              <p>期限</p>
              <button>完了</button>
              <button>変更</button>
              <button>削除</button>
            </div>
          </li>
        </ol>
      </div>
      <div className="complete-area">
        <p>完了したTodoリスト</p>
        <ol>
          {completeTodos.map((todo, index) => {
            return(

              <li>
              <div key={todo} className="list-row">
                <p>{todo}</p>
                <p>完了日</p>
              </div>
            </li>
              )
          })}
        </ol>
      </div>
    </>
  );
};
